# 创建数据库:
CREATE DATABASE `mineshop` DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci;
use mineshop;

-- ----------------------------
--  Table structure for `mine_user`
-- ----------------------------

DROP TABLE IF EXISTS `mine_user`;

CREATE TABLE `mine_user` (
  `user_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '用户id',
  `username` varchar(50) NOT NULL COMMENT '用户名',
  `password` varchar(32) NOT NULL COMMENT '密码，加密存储',
  `phone` varchar(20) DEFAULT NULL COMMENT '注册手机号',
  `email` varchar(50) DEFAULT NULL COMMENT '注册邮箱',
  `crt_time` int(10) NOT NULL DEFAULT 0 COMMENT '创建时间',
  `mod_time` int(10) NOT NULL DEFAULT 0 COMMENT '最后一次修改时间',
  `mail_code`  varchar(64) NOT NULL DEFAULT '' COMMENT '邮箱激活码' ,
  `phone_code`  varchar(6) NOT NULL DEFAULT '' COMMENT '短信激活码' ,
  `act_status`  tinyint(1) NOT NULL DEFAULT 0 COMMENT '激活状态（0=>待激活，1=>启用）' ,
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `username` (`username`) USING BTREE,
  UNIQUE KEY `phone` (`phone`) USING BTREE,
  UNIQUE KEY `email` (`email`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci COMMENT='用户表' ROW_FORMAT=DYNAMIC;

-- ----------------------------
-- Table structure for t_user_receive
-- ----------------------------

DROP TABLE IF EXISTS `mine_user_receive`;

CREATE TABLE `mine_user_receive` (
  `receive_id` int(20) NOT NULL AUTO_INCREMENT COMMENT '收货id',
  `user_id` int(20) DEFAULT NULL COMMENT '用户id',
  `contact` varchar(50) DEFAULT '' COMMENT '收货人姓名',
  `tel` varchar(20) DEFAULT '' COMMENT '收货人电话',
  `receive_province` varchar(255) DEFAULT '' COMMENT '收货地址：省',
  `receive_city` varchar(255) DEFAULT '' COMMENT '收货地址：城市',
  `receive_county` varchar(255) DEFAULT '' COMMENT '收货地址：县/区',
  `receive_address` varchar(255) DEFAULT '' COMMENT '收货地址 详细地址',
  `crt_time` int(10) NOT NULL DEFAULT 0 COMMENT '创建时间',
  `mod_time` int(10) NOT NULL DEFAULT 0 COMMENT '最后一次修改时间',
  `default_flag` tinyint(1) DEFAULT '0' COMMENT '默认标志（0=>否，1=>是）',
  `del_status` tinyint(1) DEFAULT '1' COMMENT '删除状态（0=>已删除，1=>未删除）',
  PRIMARY KEY (`receive_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci COMMENT='送货表' ROW_FORMAT=DYNAMIC;

-- ----------------------------
--  Table structure for `mine_index_ad`
-- ----------------------------

DROP TABLE IF EXISTS `mine_index_ad`;

CREATE TABLE `mine_index_ad` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `ad_type` tinyint(1) DEFAULT '0' COMMENT '广告分类（0=>轮播图，1=>其他）',
  `alt` varchar(5) DEFAULT NULL COMMENT '提示',
  `href` varchar(500) DEFAULT NULL COMMENT '网址',
  `image_url` varchar(200) DEFAULT NULL COMMENT '图片路径',
  `del_status` tinyint(1) DEFAULT '1' COMMENT '删除状态（0=>已删除，1=>未删除）',
  `order` int(4) DEFAULT '1' COMMENT '排序',
  `crt_time` int(10) NOT NULL DEFAULT 0 COMMENT '创建时间',
  `mod_time` int(10) NOT NULL DEFAULT 0 COMMENT '最后一次修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci COMMENT='首页轮播广告表' ROW_FORMAT=DYNAMIC;

