-- ----------------------------
--  Table structure for `mine_hot_words`
-- ----------------------------

DROP TABLE IF EXISTS `mine_hot_words`;

CREATE TABLE `mine_hot_words` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `display_type` tinyint(1) DEFAULT 0 COMMENT '展示类型（0=>正常，1=>加红, 2=>搜索框显示）',
  `name` varchar(200) DEFAULT NULL COMMENT '热门词',
  `url` varchar(100) DEFAULT NULL COMMENT '跳转网页',
  `word_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '热门词状态（0=>搜索，1=>指定网页）',
  `crt_time` int(10) NOT NULL DEFAULT 0 COMMENT '创建时间',
  `mod_time` int(10) NOT NULL DEFAULT 0 COMMENT '最后一次修改时间',
  PRIMARY KEY (`id`),
  KEY `mod_time` (`mod_time`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci COMMENT='热门词表' ROW_FORMAT=DYNAMIC;

-- ----------------------------
--  Table structure for `mine_category`
-- ----------------------------

DROP TABLE IF EXISTS `mine_category`;

CREATE TABLE `mine_category` (
  `category_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '分类id',
  `category_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '分类类型（0=>纵向分类，1=>横向分类（快捷入口））',
  `name` varchar(200) DEFAULT NULL COMMENT '名称',
  `order` int(4) NOT NULL DEFAULT '1' COMMENT '排列序号，表示同级类目的展现次序，如数值相等则按名称次序排列。取值范围:大于零的整数',
  `crt_time` int(10) NOT NULL DEFAULT 0 COMMENT '创建时间',
  `mod_time` int(10) NOT NULL DEFAULT 0 COMMENT '最后一次修改时间',
  PRIMARY KEY (`category_id`),
  KEY `mod_time` (`mod_time`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci COMMENT='分类表' ROW_FORMAT=DYNAMIC;

