-- ----------------------------
--  Table structure for `mine_category_secondary`
-- ----------------------------

DROP TABLE IF EXISTS `mine_category_secondary`;

CREATE TABLE `mine_category_secondary` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '类目id',
  `category_id` bigint(20) NOT NULL COMMENT '分类id',
  `parent_id` bigint(20) DEFAULT NULL COMMENT '父类目ID=0时，代表的是一级的类目',
  `name` varchar(200) DEFAULT NULL COMMENT '名称',
  `href` varchar(500) DEFAULT NULL COMMENT '网址',
  `del_status` tinyint(1) DEFAULT '1' COMMENT '删除状态（0=>已删除，1=>未删除）',
  `display_type` tinyint(1) DEFAULT 0 COMMENT '展示类型（0=>正常，1=>加红, 2=>搜索框显示）',
  `order` int(4) DEFAULT '1' COMMENT '排列序号，表示同级类目的展现次序，如数值相等则按名称次序排列。取值范围:大于零的整数',
  `crt_time` int(10) NOT NULL DEFAULT 0 COMMENT '创建时间',
  `mod_time` int(10) NOT NULL DEFAULT 0 COMMENT '最后一次修改时间',
  PRIMARY KEY (`id`),
  KEY `parent_id` (`parent_id`,`del_status`) USING BTREE,
  KEY `order` (`order`),
  KEY `cid` (`category_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci COMMENT='次级分类表' ROW_FORMAT=DYNAMIC;

